import React, { Component } from 'react'

export default class Timer extends Component {
  constructor(props) {
    super(props);
    this.state = { timer: 3000 }
  }
  componentDidMount() {
    this.resetTimer();
  }
  resetTimer() {
    setInterval(() => this.setState({ timer: this.state.timer - 1 }), 1000)
  }
  render() {
    // min = 
    var time = ((this.state.timer / 60) * 1000).floor //.toString(); // + ":" + (this.state.timer % 60).toString()
    return (
      <div className="timer">
        { time } 
      </div>
    )
  }
}
