import React, { Component } from 'react'

export default class Timer extends Component {
  constructor(props) {
    super(props);
    this.state = { timer: 3000 }
  }

  resetTimer() {
    var timer;
    const resetTimer = () => timer = 3000
    resetTimer()
    setInterval(() => timer -= 1, 1000)

  }
  render() {

    return (
      <div className="timer">
        { timer } 
      </div>
    )
  }
}
