export class Speed extends Component {
    constructor(props) {
        super(props);

        this.state={ prompt, new_state: '' }
        this.handleKeyDown = this.handleKeyDown.bind(this);
    }

    handleKeyDown(e) {
        let prompt = Object.values(this.props)[0] || '';
        let new_state = this.state.new_state
        console.log(new_state);
        switch(e) {
            case 'Backspace':
                new_state = new_state.slice
                break;
            default: 
                new_state = new_state.concat(e.key)
                this.setState({ new_state, prompt })
                console.log(this.state)
                break;
        }



    }
    render() {
    window.addEventListener("keydown", this.handleKeyDown);

    function log(event) {
        console.log(event);

    }
    //   debugger
    const prompt = Object.values(this.props)
    return (
      <div onKeyDown={this.handleKeyDown}>
        {prompt}


      </div>
    )
  }
}

export default Speed
